var cal=require("../lib/calculator").createCalculator();
var tools = require('argv');
var argvs = process.argv;
var i=0;
for(;i<argvs.length;i++){
    if(argvs[i][0]=="-"){
       break;
    }
}
var option=argvs[i++].substr(1);
var numA=tools.types.int(argvs[i++]);
var numB=tools.types.int(argvs[i++]);
var result=cal[option](numA,numB);
console.log(result);


